import React, { useState } from 'react';
import styled from 'styled-components';

const PopupOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const PopupContent = styled.div`
  background: white;
  padding: 30px;
  border-radius: 15px;
  width: 90%;
  max-width: 500px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 25px;
`;

const FormSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const FormLabel = styled.label`
  color: #666;
  font-size: 14px;
  font-weight: 500;
`;

const Input = styled.input`
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 16px;
  width: 100%;

  &:focus {
    outline: none;
    border-color: #4a90e2;
  }
`;

const TextArea = styled.textarea`
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 16px;
  min-height: 100px;
  resize: vertical;
`;

const Button = styled.button`
  padding: 12px 24px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  cursor: pointer;
  transition: background 0.2s;

  &:hover {
    background: #357abd;
  }
`;

const ImagePreview = styled.div`
  width: 100%;
  height: 200px;
  border: 2px dashed #ddd;
  border-radius: 8px;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  background-size: cover;
  background-position: center;
`;

const MentionsContainer = styled.div`
  position: relative;
  width: 100%;
`;

const MentionsDropdown = styled.div<{ isVisible: boolean }>`
  position: absolute;
  top: calc(100% + 5px);
  left: 0;
  right: 0;
  background: white;
  border: 1px solid #ddd;
  border-radius: 8px;
  max-height: 200px;
  overflow-y: auto;
  display: ${props => props.isVisible ? 'block' : 'none'};
  z-index: 1001;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const MentionOption = styled.div`
  padding: 12px 15px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 12px;

  &:hover {
    background: #f5f5f5;
  }

  &:first-child {
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
  }

  &:last-child {
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
  }
`;

const MentionAvatar = styled.div`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: linear-gradient(90deg, #4a90e2 0%, #7b1fa2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 500;
  font-size: 16px;
`;

const SelectedMentions = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 12px;
`;

const MentionTag = styled.div`
  background: #e3f2fd;
  color: #1976d2;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 14px;
  display: flex;
  align-items: center;
  gap: 8px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05);

  button {
    background: none;
    border: none;
    color: #1976d2;
    cursor: pointer;
    padding: 0;
    font-size: 18px;
    display: flex;
    align-items: center;
    opacity: 0.7;
    transition: opacity 0.2s;

    &:hover {
      opacity: 1;
    }
  }
`;

interface CreatePostProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (post: any) => void;
}

// Lista simulada de usuarios del chat familiar
const familyMembers = [
  { id: 1, name: 'Mamá', username: 'mama' },
  { id: 2, name: 'Papá', username: 'papa' },
  { id: 3, name: 'Hermano', username: 'hermano' },
  { id: 4, name: 'Hermana', username: 'hermana' },
];

const CreatePost: React.FC<CreatePostProps> = ({ isOpen, onClose, onSubmit }) => {
  const [description, setDescription] = useState('');
  const [image, setImage] = useState<File | null>(null);
  const [location, setLocation] = useState('');
  const [mentionSearch, setMentionSearch] = useState('');
  const [showMentions, setShowMentions] = useState(false);
  const [selectedMentions, setSelectedMentions] = useState<typeof familyMembers[0][]>([]);
  const [imagePreviewUrl, setImagePreviewUrl] = useState('');

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImage(file);
      setImagePreviewUrl(URL.createObjectURL(file));
    }
  };

  const handleMentionSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setMentionSearch(value);
    setShowMentions(true);
  };

  const handleSelectMention = (member: typeof familyMembers[0]) => {
    if (!selectedMentions.find(m => m.id === member.id)) {
      setSelectedMentions([...selectedMentions, member]);
    }
    setMentionSearch('');
    setShowMentions(false);
  };

  const handleRemoveMention = (memberId: number) => {
    setSelectedMentions(selectedMentions.filter(m => m.id !== memberId));
  };

  const filteredMembers = familyMembers.filter(
    member => member.name.toLowerCase().includes(mentionSearch.toLowerCase()) &&
    !selectedMentions.find(m => m.id === member.id)
  );

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const post = {
      description,
      image,
      location,
      mentions: selectedMentions,
      date: new Date().toLocaleString()
    };
    onSubmit(post);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <PopupOverlay onClick={onClose}>
      <PopupContent onClick={e => e.stopPropagation()}>
        <h2>Crear nueva publicación</h2>
        <Form onSubmit={handleSubmit}>
          <FormSection>
            <FormLabel htmlFor="image-input">Imagen</FormLabel>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: 'none' }}
              id="image-input"
            />
            <label htmlFor="image-input">
              <ImagePreview
                style={imagePreviewUrl ? { backgroundImage: `url(${imagePreviewUrl})` } : {}}
              >
                {!imagePreviewUrl && 'Haz clic para agregar una imagen'}
              </ImagePreview>
            </label>
          </FormSection>
          
          <FormSection>
            <FormLabel htmlFor="description">Descripción</FormLabel>
            <TextArea
              id="description"
              placeholder="¿Qué quieres compartir?"
              value={description}
              onChange={e => setDescription(e.target.value)}
            />
          </FormSection>
          
          <FormSection>
            <FormLabel htmlFor="location">Ubicación</FormLabel>
            <Input
              id="location"
              placeholder="Agrega una ubicación"
              value={location}
              onChange={e => setLocation(e.target.value)}
            />
          </FormSection>
          
          <FormSection>
            <FormLabel htmlFor="mentions">Etiquetar personas</FormLabel>
            <MentionsContainer>
              <Input
                id="mentions"
                placeholder="Buscar personas para etiquetar"
                value={mentionSearch}
                onChange={handleMentionSearch}
                onFocus={() => setShowMentions(true)}
              />
              <MentionsDropdown isVisible={showMentions && filteredMembers.length > 0}>
                {filteredMembers.map(member => (
                  <MentionOption
                    key={member.id}
                    onClick={() => handleSelectMention(member)}
                  >
                    <MentionAvatar>{member.name[0]}</MentionAvatar>
                    <span>{member.name}</span>
                  </MentionOption>
                ))}
              </MentionsDropdown>
              <SelectedMentions>
                {selectedMentions.map(member => (
                  <MentionTag key={member.id}>
                    {member.name}
                    <button 
                      type="button"
                      onClick={() => handleRemoveMention(member.id)}
                    >
                      ×
                    </button>
                  </MentionTag>
                ))}
              </SelectedMentions>
            </MentionsContainer>
          </FormSection>
          
          <Button type="submit">Publicar</Button>
        </Form>
      </PopupContent>
    </PopupOverlay>
  );
};

export default CreatePost;
