import React, { useState } from 'react';
import styled from 'styled-components';
import Posts from './components/Posts';
import CreatePost from './components/CreatePost';
import Filters from './components/Filters';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #4a90e2 0%, #7b1fa2 100%);
  display: flex;
`;

const Sidebar = styled.div`
  width: 80px;
  background: rgba(0, 0, 0, 0.2);
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 20px;
  display: flex;
  gap: 20px;
`;

const ContentArea = styled.div`
  flex: 1;
  max-width: 800px;
`;

const SidebarIcon = styled.div`
  width: 40px;
  height: 40px;
  background: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

const CreateButton = styled.button`
  position: fixed;
  bottom: 30px;
  right: 30px;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: #4a90e2;
  color: white;
  border: none;
  font-size: 24px;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.1);
  }
`;

const App: React.FC = () => {
  const [isCreatePostOpen, setIsCreatePostOpen] = useState(false);

  const handleCreatePost = (post: any) => {
    console.log('New post:', post);
    // Here you would typically send the post to your backend
  };

  return (
    <AppContainer>
      <Sidebar>
        <SidebarIcon>üè†</SidebarIcon>
        <SidebarIcon>üí¨</SidebarIcon>
        <SidebarIcon>üì∑</SidebarIcon>
        <SidebarIcon>üë§</SidebarIcon>
      </Sidebar>

      <MainContent>
        <ContentArea>
          <h1 style={{ color: 'white', marginBottom: '20px' }}>Publicaciones</h1>
          <Posts />
        </ContentArea>
        
        <Filters onFilterChange={(filters) => console.log('Filters:', filters)} />
      </MainContent>

      <CreateButton onClick={() => setIsCreatePostOpen(true)}>+</CreateButton>

      <CreatePost
        isOpen={isCreatePostOpen}
        onClose={() => setIsCreatePostOpen(false)}
        onSubmit={handleCreatePost}
      />
    </AppContainer>
  );
};

export default App;
